import React, {Component} from 'react';
import FormValidator from '../validation/FormValidator';
import axios from 'axios';

class FormStats extends Component{
constructor(){
super();
this.validator = new FormValidator([{
    field: 'health',
    method: 'isEmpty',
    validWhen: false,
    message: 'Enter health'
}, {
    field: 'maxHealth',
    method: 'isEmpty',
    validWhen: false,
    message: 'Enter max health'
}, {
    field: 'mana',
    method: 'isEmpty',
    validWhen: false,
    message: 'Enter mana'
}, 
{
    field: 'maxMana',
    method: 'isEmpty',
    validWhen: false,
    message: 'Enter max mana'
}
]);

this.state = {
// health: this.props.health,
// maxHealth: this.props.maxHealth,
// mana: this.props.mana,
// maxMana: this.props.maxMana,
validation: this.validator.valid()
}
this.submitted = false;
}
handleInputChange = event => {
event.preventDefault();
this.setState({
[event.target.name]: event.target.value,
});
}
handleFormSubmit = event => {
event.preventDefault();
const validation = this.validator.validate(this.state);
// console.log(this.props.id_desc);
this.setState({
validation
});
this.submitted = true;
if(validation.isValid) {
    console.log("Validated Item Form !");

    axios.defaults.xsrfCookieName = 'csrftoken';
    axios.defaults.xsrfHeaderName = 'X-CSRFToken';

    axios.get(`http://127.0.0.1:8000/user/item_create/`+ this.state.item_name + "/" + this.state.item_effect + "/" + this.state.type_object + "/" + this.props.id_desc + "/" + this.state.item_nbr)
    .then(res => {
        console.log(res);
        this.setState({ updateInventory: !this.state.updateInventory });
    }).then(res => {
        this.props.handler();
    })
}
}
render() {
let validation = this.submitted ?this.validator.validate(this.state) : this.state.validation
return (
<div>
    <div className="container form_item form">
        <div className="row">
            <div>
                <form className="registrationForm">
                <h2>Change stats</h2>
                <div>
                    <div className={validation.item_nbr.isInvalid ? 'has-error' : null}>
                        <label htmlFor="item_nbr">Health</label>
                        <input type="integer" className="form-control" name="item_nbr" placeholder="Item nbr" onChange={this.handleInputChange} /> <span className="help-block">{validation.item_nbr.message}</span> 
                    </div>
                    /
                    <div className={validation.item_nbr.isInvalid ? 'has-error' : null}>
                        <label htmlFor="item_nbr">Max Health</label>
                        <input type="integer" className="form-control" name="item_nbr" placeholder="Item nbr" onChange={this.handleInputChange} /> <span className="help-block">{validation.item_nbr.message}</span> 
                    </div>
                </div>
                <div>
                    <div className={validation.item_nbr.isInvalid ? 'has-error' : null}>
                        <label htmlFor="item_nbr">Mana</label>
                        <input type="integer" className="form-control" name="item_nbr" placeholder="Item nbr" onChange={this.handleInputChange} /> <span className="help-block">{validation.item_nbr.message}</span> 
                    </div>
                    /
                    <div className={validation.item_nbr.isInvalid ? 'has-error' : null}>
                        <label htmlFor="item_nbr">Max Mana</label>
                        <input type="integer" className="form-control" name="item_nbr" placeholder="Item nbr" onChange={this.handleInputChange} /> <span className="help-block">{validation.item_nbr.message}</span> 
                    </div>
                </div>


                <button onClick={this.handleFormSubmit} className="btn btn-primary"> Create item </button>
                </form>
            </div>
        </div>
    </div>

</div>
)
}
}
export default FormStats;
