import React from 'react';

class Dices extends React.Component {
  constructor() {
    super();
    this.state = {
      resultDice: false,
      selectedDice: 6
    };
  }
  rollDice(val) {
    this.setState({
      selectedDice: val
    });

  }
    render() {
      return (
        <div className='dices'>
          <button onClick={this.props.closePopup}>close me</button>

          <button onClick={this.rollDice.bind(6)}>6</button>
        </div>
      );
    }
  }

export default Dices;
