import './App.css';
// import  { BrowserRouter, Link, Routes, Route } from "react-router-dom";
import Dices from "./components/Dices";
import React from 'react';
import axios from 'axios';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      showPopup: false,
      test: []
    };
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }

  getUsers = () => {
    var listUsers = [];
    for(let i = 0; i < this.state.test.length; i++) {
    //  listUsers += this.state.test[i].Nom;
     listUsers.push(this.state.test[i].Nom);
    }
    // console.log(listUsers);
    return (
      listUsers
    )
  };
  componentDidMount() {
    // let res = axios.get('https://localhost:8000/api/config');
	  // console.log(res);
    axios.defaults.xsrfCookieName = 'csrftoken'
    axios.defaults.xsrfHeaderName = 'X-CSRFToken'
	  // axios.defaults.headers['x-csrf-token'] = res.data.csrf_token;

    axios.get(`http://127.0.0.1:8000/users`)
      .then(res => {
        let result = Object.values(res.data);
        this.setState({ test: result });
        // console.log(result);
      })
  }




  render() {
    var users = this.getUsers();
    return (
      <div className="App">


        {users.map(function(user, i) {
          return <div>{user} {i}</div>;
        })}
        <button onClick={this.togglePopup.bind(this)}>show popup</button>

        {this.state.showPopup ? 
          <Dices
            text='Dices'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
      </div>
    );
  }

}

export default App;
