import React from 'react';
import axios from 'axios';

class CardCharacters extends React.Component {
  constructor() {
    super();
    this.state = {
        users: []
    };
  }

  getUsers = () => {
    var listUsers = [];
    for(let i = 0; i < this.state.users.length; i++) {
     listUsers.push(this.state.users[i]);
    }
    return (
      listUsers
    )
  };

  componentDidMount() {
    axios.defaults.xsrfCookieName = 'csrftoken'
    axios.defaults.xsrfHeaderName = 'X-CSRFToken'

    axios.get(`http://127.0.0.1:8000/users`)
      .then(res => {
        let result = Object.values(res.data);
        this.setState({ users: result });
      })
  }

    render() {

        var users = this.getUsers();

        return (
            <div className='cardCharacters'>
            {users.map(function(user, i) {
            return (
                <div class="card" style="width: 18rem;" key={i}>
                    <div class="card-body">
                        <h5 class="card-title">{user.Nom}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">{user.Race}</h6>
                        <p class="card-text">{user.Spécial}</p>
                        <h6 class="card-subtitle mb-2">{user.Passif}</h6>
                        <h6 class="card-subtitle mb-2">{user.Malus}</h6>
                    </div>
                </div>
                // <div>
                //     <div>{user.Nom}</div>
                //     <div>{user.Race}</div>
                //     <div>{user.Spécial}</div>
                //     <div>{user.Passif}</div>
                //     <div>{user.Malus}</div>
                // </div>
            )})}
            </div>
        );
    }
  }

export default CardCharacters;
