// import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './assets/App.css';
import './assets/cards.css';
import './assets/description.css';
import './assets/dices.css';
import './assets/forms.css';
import './assets/navbar.css';
import './assets/group.css';



import './scripts/script';

import SelectParty from "./components/selectParty";
import Homepage from "./components/HomePage";
import Dices from "./components/Dices";
import Navbar from "./components/navbar";
import FormCharacter from './components/FormCharacter';
import React from 'react';


class App extends React.Component {
  constructor() {
    super();
    this.getLogged = this.getLogged.bind(this);
    this.selectParty = this.selectParty.bind(this);
    this.state = {
      logged: false,
      id_user: 0,
      username: "",
      email: "",
      party: 0
    };
  }

  getLogged = (username, email, id) => {
    this.setState({ 
      logged: !this.state.logged,
      id_user: id,
      username: username,
      email: email
     });
  }

  selectParty = (id) => {
    console.log(id);
    this.setState({
      party: id
     });
    // this.setState({ party: id });
    console.log(this.state);
  }



  render() {

    return (

      <div className="global_app">
          {this.state.logged && this.state.party ? 
            <div className="App">
              <Navbar/>
              <FormCharacter/>
              <Dices/>
              <div className="content_center"></div>
            </div>

            : this.state.logged ?

            <SelectParty selectParty={this.selectParty}/>
            // <div>
            //   <p>Select party</p>
            //   <button onClick={this.selectParty.bind(this.selectParty, 2)}>Select</button>
            // </div>

            :

            <Homepage 
            logged={this.state.logged}
            getLogged={this.getLogged}
            />
          }

      </div>

      // <div className="App">
      //     {this.state.logged ? 
      //       <Navbar/>
      //       <FormCharacter/>
      //       <Dices/>
      //       <div className="content_center"></div>

      //       :

      //       <Homepage/>
      //     }

      // </div>
    );
  }

}

export default App;
