import React from 'react';
import axios from 'axios';
import Description from './description';


class GroupActif extends React.Component {
  constructor() {
    super();
    this.state = {
        users: [],
        displayDesc: false,
        id_desc: 0
    };
  }

  getUsers = () => {
    var listUsers = [];
    for(let i = 0; i < this.state.users.length; i++) {
     listUsers.push(this.state.users[i]);
    }
    return (
      listUsers
    )
  };

  handleClick = (id) => {

    axios.defaults.xsrfCookieName = 'csrftoken'
    axios.defaults.xsrfHeaderName = 'X-CSRFToken'
    axios.get(`http://127.0.0.1:8000/user/getSpells/`+id)
      .then(res => {
        console.log(res);
        //   Update self
        //   And update the group (child component)
        // this.setState({ update: !this.state.update });           
        // this.setState({ updateGroup: !this.state.updateGroup });
    })
    axios.get(`http://127.0.0.1:8000/user/getInventory/`+id)
      .then(res => {
        console.log(res);
        //   Update self
        //   And update the group (child component)
        // this.setState({ update: !this.state.update });           
        // this.setState({ updateGroup: !this.state.updateGroup });
    })
    // console.log(id);
    console.log(this.state.displayDesc);
    if(id !== this.state.id_desc) {
      this.setState({ id_desc: id });
    }else if(id !== this.state.id_desc && this.state.displayDesc) {
      this.setState({ id_desc: id });
      this.setState({ displayDesc: !this.state.displayDesc });
    }else {
      this.setState({ displayDesc: !this.state.displayDesc });
    }

  };

  componentDidUpdate(prevProps, prevState, snapshot) {

      // Update the component if parent state is updated
      if(this.props.updateGroup !== prevProps.updateGroup) {
        axios.defaults.xsrfCookieName = 'csrftoken';
        axios.defaults.xsrfHeaderName = 'X-CSRFToken';

        axios.get(`http://127.0.0.1:8000/users/active`)
        .then(res => {
            let result = Object.values(res.data);
            this.setState({ users: result });
        })
      }
  }

  componentDidMount() {
    axios.defaults.xsrfCookieName = 'csrftoken'
    axios.defaults.xsrfHeaderName = 'X-CSRFToken'

    axios.get(`http://127.0.0.1:8000/users/active`)
      .then(res => {
        let result = Object.values(res.data);
        this.setState({ users: result });
      })
  }

    render() {

        var users = this.getUsers();

        return (
            <div className='groupActif'>
                {users.map((user, i) => {
                return (
                    <div className={"card " + (user.active == 2 ? "active" : "unactive") } key={i} onClick={this.handleClick.bind(this.handleClick, user.id)}>
                        <div className="card-name">{user.Nom}</div>
                        <div className="card-race">{user.Race}</div>
                        <div className="card-spe">{user.Spécial}</div>
                        <div className="card-passif">{user.Passif}</div>
                        <div className="card-malus">{user.Malus}</div>
                    </div>
                )})}


          {this.state.displayDesc ? 
            <Description displayDesc={this.state.displayDesc}/> : null
          }

            </div>
        );
    }
  }

export default GroupActif;
