import React from 'react';

class Dices extends React.Component {
  constructor() {
    super();
    this.state = {
      resultDice: 0,
      selectedDice: 0
    };
  }
  rollDice = () => {
    let min = Math.ceil(1);
    let max = Math.floor(this.state.selectedDice);
    let result = Math.floor(Math.random() * (max - min +1)) + min;
    this.setState({ resultDice: result });
  }
  // Change the selected dice
  handleChange = (e) => {
    this.setState({ selectedDice: e.target.value });
  };
    render() {
      return (
        <div className='dices'>

          <button value="4" onClick={this.handleChange}>4</button>
          <button value="6" onClick={this.handleChange}>6</button>
          <button value="8" onClick={this.handleChange}>8</button>
          <button value="10" onClick={this.handleChange}>10</button>
          <button value="20" onClick={this.handleChange}>20</button>
          <button value="100" onClick={this.handleChange}>100</button>

          <p>{this.state.selectedDice}</p>
          <p>Result : {this.state.resultDice}</p>
          <button onClick={this.rollDice}>Show</button>
        </div>
      );
    }
  }

export default Dices;
