import React from 'react';

class Dices extends React.Component {
  constructor() {
    super();
    this.state = {
      resultDice: 0,
      selectedDice: 0
    };
  }
  rollDice = () => {
    let min = Math.ceil(1);
    let max = Math.floor(this.state.selectedDice);
    let result = Math.floor(Math.random() * (max - min +1)) + min;
    this.setState({ resultDice: result });
  }
  // Change the selected dice
  handleChange = (e, id_btn) => {
    console.log(id_btn);
    this.setState({ selectedDice: e.target.value });
  };
    render() {
      return (
        <div className='dices'>
          <div className="content_btn_dices">
            <button id="btn_4" value="4" onClick={this.handleChange.bind(this.handleChange, "btn_4")}>4</button>
            <button id="btn_6" value="6" onClick={this.handleChange.bind(this.handleChange, "btn_6")}>6</button>
            <button id="btn_8" value="8" onClick={this.handleChange.bind(this.handleChange, "btn_8")}>8</button>
            <button id="btn_10" value="10" onClick={this.handleChange.bind(this.handleChange, "btn_10")}>10</button>
            <button id="btn_20" value="20" onClick={this.handleChange.bind(this.handleChange, "btn_20")}>20</button>
            <button id="btn_100" value="100" onClick={this.handleChange.bind(this.handleChange, "btn_100")}>100</button>
          </div>
          <p>{this.state.selectedDice}</p>
          <p>Result : {this.state.resultDice}</p>
          <button onClick={this.rollDice}>Show</button>
        </div>
      );
    }
  }

export default Dices;
