import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './assets/App.css';
import './assets/cards.css';
import './assets/description.css';
import './assets/dices.css';
import './assets/forms.css';


import './scripts/script';

// import  { BrowserRouter, Link, Routes, Route } from "react-router-dom";
import Dices from "./components/Dices";
import Navbar from "./components/navbar";
import FormCharacter from './components/FormCharacter';
import React from 'react';
import axios from 'axios';


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      showPopup: false
    };
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }








  render() {

    return (
      <div className="App">
        <Navbar/>
        <FormCharacter/>
        {/* <CardCharacters/> */}

        <button className="button_dice" onClick={this.togglePopup.bind(this)}>Dices</button>
        <button className="button_form_character" >New Character</button>

        {this.state.showPopup ? 
          <Dices
            text='Dices'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
      </div>
    );
  }

}

export default App;
