import React from 'react';
import $ from 'jquery';
import axios from 'axios';

class SelectParty extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        parties: []
    };
  }

  componentDidMount() {
    this.getParties(this.props.id_user);
  }

  handeClick = (id) => {
    this.props.selectParty(id);
  }


  getParties = (id) => {
    axios.defaults.xsrfCookieName = 'csrftoken'
    axios.defaults.xsrfHeaderName = 'X-CSRFToken'

    axios.get(`http://127.0.0.1:8000/api/user/getParties` + `/` + id)
      .then(res => {
        let result = Object.values(res.data);
        // console.log(result);
        this.setState({ parties: res.data }); 
        return result;
    })
  }

    render() {
    // var parties = this.getParties(this.props.id_user);
    // console.log(parties);
        return (
            <div>
            {parties.map((partie, i) => {
                return (
                <div>
                    <p>{partie.name}</p>
                    <p>{partie.nbr_player} players</p>
                    <button onClick={this.handeClick.bind(this.handeClick, partie.id)}>Select</button>
                </div>
            )})}
            </div>
        );
    }
  }

export default SelectParty;
