import React from 'react';

class Dices extends React.Component {
  constructor() {
    super();
    this.state = {
      resultDice: 0,
      selectedDice: 0
    };
  }
  rollDice = () => {
    // alert(this.state.selectedDice);
    let min = Math.ceil(0);
    let max = Math.floor(this.state.selectedDice);
    let result = Math.floor(Math.random() * (max - 1)) + 1;
    this.setState({ resultDice: result });
  }
  handleChange = (e) => {
    this.setState({ selectedDice: e.target.value });
  };
    render() {
      return (
        <div className='dices'>
          <button onClick={this.props.closePopup}>close me</button>

          <button value="6" onClick={this.handleChange}>6</button>
          <button value="2" onClick={this.handleChange}>2</button>
          <p>{this.state.selectedDice}</p>
          <p>Result : {this.state.resultDice}</p>
          <button onClick={this.rollDice}>Show</button>
        </div>
      );
    }
  }

export default Dices;
