import React from 'react';
import axios from 'axios';
import FormItem from './itemForm';
import $ from 'jquery';

class Description extends React.Component {
  constructor(props) {
    super(props);
    this.handler = this.handler.bind(this);
    this.state = {
      spells: [],
      items: [],
      update: false
    };
  }

  handler() {
    this.setState({
      update: !this.state.update
    })
  }

  fetchData() {
    axios.defaults.xsrfCookieName = 'csrftoken';
    axios.defaults.xsrfHeaderName = 'X-CSRFToken';

    axios.get(`http://127.0.0.1:8000/user/getSpells/`+ this.props.id_desc)
    .then(res => {
      //   Update self
      //   And update the group (child component)
      this.setState({ spells: res.data });  
      // console.log(this.state.spells);      
    })
    axios.get(`http://127.0.0.1:8000/user/getInventory/`+ this.props.id_desc)
      .then(res => {
        //   Update self
        //   And update the group (child component)
        this.setState({ items: res.data });
        // console.log(this.state.items);         
    })
  }
  componentDidUpdate(prevProps, prevState, snapshot) {
    // Update the component if parent state is updated
    if(this.props.id_desc !== prevProps.id_desc || this.state.update !== prevState.update) {
      this.fetchData();
    }
}

  componentDidMount() {
    this.fetchData();
    this.getScripts();
  }

  getScripts() {
    var button_form_item = document.getElementsByClassName('btn_form_item');
    var button_form_spell = document.getElementsByClassName('btn_form_spell');
    var form_item = document.getElementsByClassName('form_item');
    var form_character = document.getElementsByClassName('form_character');
    var card_group = document.getElementsByClassName('card_group');
    var forms = document.getElementsByClassName('form');

    $(button_form_item).on('click', function() {
        if($(form_character).hasClass('displayed')) {
            $(form_character).removeClass('displayed');
        }

        if($(form_item).hasClass("displayed")) {
            $(form_item).removeClass("displayed"); 
        }else {
            $(form_item).addClass("displayed"); 
        }
    })

    $(card_group).on('click', function() {
      if($(form_character).hasClass('displayed')) {
          $(form_character).removeClass('displayed');
      }
  })

    $(button_form_spell).on('click', function() {
        alert("btn_spell");
    })
  }



  getItems = () => {
    var listItems = [];
    for(let i = 0; i < this.state.items.length; i++) {
     listItems.push(this.state.items[i]);
    }
    return (
      listItems
    )
  };
  getSpells = () => {
    var listSpells = [];
    for(let i = 0; i < this.state.spells.length; i++) {
     listSpells.push(this.state.spells[i]);
    }
    return (
      listSpells
    )
  };










    render() {
      var items = this.getItems();
      var spells = this.getSpells();
      return (
        <div className='description'>
            <button className="btn_form_item">Add item</button>
            <button className="btn_form_spell">Add spell</button>
            <div className="item_content">
              {items.map((item, i) => {
                      return (
                          <div className="item_box" key={i}>
                              <div className="item-name">{item.itemName}</div>
                              <div className="item-race">{item.itemEffect}</div>
                              <div className="item-spe">{item.type_object}</div>
                              <div className="item-nbr">{item.nbr}</div>
                          </div>
              )})}
            </div>
            <div className="spell_content">
              {spells.map((spell, i) => {
                      return (
                          <div className="spell_box" key={i}>
                              <div className="card-name">{spell.spellName}</div>
                              <div className="card-race">{spell.spellEffect}</div>
                          </div>
              )})}
            </div>
              <FormItem id_desc={this.props.id_desc} handler={this.handler}/>

        </div>
      );
    }
  }

export default Description;
